qps = mockRequest.getQueryString().split('&')

def mapParams = qps.collectEntries { params -> params.split('=').collect { URLDecoder.decode (it) }}

if (mapParams ['delay']) {
    for ( String values : qps ) {
        if ( values.contains ("delay=") ) {
            def delay = (values.split("=") [1] as int ) 
                log.info ('delay') 
                sleep (delay)
        }
    }
} else {
    int time = 200
    log.info ('NoDelay')
sleep (time)
}
